export async function getNotifications(): Promise<NierNotification[]> {
  const notices = notifications.map(notification => {
    const notice = notificationsContent[notification.informationId]

    return {
      ...notification,
      body: notice.body.replaceAll('/images/', 'https://web.app.nierreincarnation.com/images/')
    }
  })

  return notices;
}

export async function getSingleNotification(id: number): Promise<NierNotification> {
  return notifications[id];
}


